import React, { useState } from 'react'
import './App.css';
import data from './users.json'
import logo from '../src/linkedin.png'
import { render } from '@testing-library/react';

function App() {

  //state for list of users
  const setInitialValue = () => {
    return data
  }
  const [users, setUsers] = useState(() => setInitialValue())
  //state for search bar
  const [search, setSearch] = useState('')
// state for teacher search
const [isTeacher, setIsTeacher] = useState('false')


  //search bar
  const handleSearch = event => {
    setSearch(event.target.value)
  }

  let newSearch = users.filter((user) => {
    return (user.firstName.toLowerCase() + user.lastName.toLowerCase()).includes(search)
  })

  //teacher and student checkboxes

  const handleTeacherSearch = event => {
    setIsTeacher(event.target.checked)
  }

  if (isTeacher == true) {
    let newSearch = users.filter((user) => {
      return user.role === 'teacher'
    }
    )}


  return (
    <div className="App">
     <h1>IronBook</h1>
      <form>
        <input type='text' value={search} onChange={handleSearch} style={{margin: '20px'}} placeholder='Search by name' />
        <label htmlFor="">Teacher:</label>
      </form>

      <table class='center'>
        <tr>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Campus</th>
          <th>Role</th>
          <th>Links</th>
        </tr>
        <tbody>
          {newSearch.map(user => {
            return (
              <tr>
                <td>{user.firstName}</td>
                <td>{user.lastName}</td>
                <td>{user.campus}</td>
                <td>{user.role}</td>
                <td>{user.linkedin && <a href={user.linkedin}> <img src={logo} style={{ width: '15px' }} /></a>}</td>
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  );
}
export default App;
